import React, { Component } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";

// MY FILES
import Vitamins from './Components/MyPortfolio/MyPortfolio';
import Menu from './Components/Menu/Menu';

class App extends Component {
  state = {
    vitaminCTotal : 0,
    vitaminDTotal : 0,
    vitaminETotal : 0,
    userInputs:[],
    currentInput:""
  }

  getInputValue = (event) => {
    const inputTotal = event.target.value
    //const vitaminDTotal = event.target.value
    console.log(inputTotal)

    this.setState({
      currentInput: inputTotal,
      //vitaminDTotal: vitaminDTotal
    })
  }
// VITAMIN C BUTTON =================================================
  submitBtn = () => { 
  // if user does not input anything, alert them
    if (this.state.currentInput.length === 0) {
      alert(" ENTER SOMETHING, YOU FOOL! ")
      return;
    }
  // if user enters something that is not a number, alert them
    if (!Number.isInteger(parseInt(this.state.currentInput))) {
      alert("INVALID INPUT, YOU IMBECILE. NUMBERS ONLYS")
      return;
    }

  var newArray = this.state.userInputs;
  var varTotal = 0;

  newArray.push(parseInt(this.state.currentInput));
  this.setState({
    currentInput:"",
    userInputs:newArray
  });

  for (let i = 0; i < newArray.length; i++) {
    varTotal += parseInt(newArray[i]);      
  }
  if (varTotal > 2000) {
    alert ("waNnA DiE?")
  } 
  this.setState({
    vitaminCTotal:varTotal
  }) 
}
//===============================================================
// VITAMIN D =================================================
  vitaminDBtn = () => { 
    if (this.state.currentInput.length === 0) {
      alert(" ENTER SOMETHING, YOU FOOL! ")
      return;
    }
    if (!Number.isInteger(parseInt(this.state.currentInput))) {
      alert("INVALID INPUT, YOU IMBECILE. NUMBERS ONLYS")
      return;
    }

  var newArray = this.state.userInputs;
  var varTotal = 0;

  newArray.push(parseInt(this.state.currentInput));
  this.setState({
    currentInput:"",
    userInputs:newArray
  });

  for (let i = 0; i < newArray.length; i++) {
    varTotal += parseInt(newArray[i]);      
  }
  if (varTotal > 2000) {
    alert ("waNnA DiE?")
  } 
  this.setState({
    vitaminDTotal:varTotal
  }) 
}


render() {
  return (
    <div>
    <Menu />
    <br></br>
    <Vitamins 
      submitBtn = {this.submitBtn}
      getInputValue ={this.getInputValue}
      vitaminC = {this.state.vitaminCTotal}
      vitaminD = {this.state.vitaminDTotal}
      vitaminDBtn = {this.vitaminDBtn}
      vitaminE = {this.state.vitaminETotal}
      currentInput = {this.state.currentInput}
    />
    </div>
    );
  }
}
export default App;
