import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Vitamins from './Components/MyPortfolio/MyPortfolio'
/*
what will the user see when they start:
//0/2000 - how much is the daily recommendation and how much they actually took that day
//search bar, we will get the value that they type in. change the state to whatever the user puts in
// when they click on submit, it will store that information in a database
// if someone goes over their limit, alert that they are at risk of side effects
// the total should be an array of numbers since they can take multiple pills a day. For loop to add up all of the numbers together. display it with the recommended amount
*/

class App extends Component {
  state = {
    vitamins : [],
    total : [],
  }

  // getInputValue = (event) => {
  //   const inputTotal = event.target.value
  //   console.log(inputTotal)
  //   this.setState({
  //     total: event.target.value
  //   })
  // }

  submitBtn = event => {
  event.preventDefault();

  var x = document.getElementById("myProgress");
  x.value = document.getElementById("value").value;

  for (let i = 0; i < this.state.total.length; i++) {
    var newArray = [x.value.slice()]
    newArray.push(x.value)  

    this.setState({
      total : newArray
    })   
    console.log(newArray)
  }
}

  // this.setState({
  //   total : [...this.state.total, x.value]
  // })  

  render() {
    return (
      <Vitamins 
        submitBtn = {this.submitBtn}
        getInputValue ={this.getInputValue}
        addTotal = {this.state.total}
      />
    );
  }
}

export default App;
